import { useState } from "react";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import { makeStyles } from "@material-ui/core/styles";
import Main from "./pages/Main";
import About from "./pages/About";
import Contacts from "./pages/Contacts";
import Feedbacks from "./pages/Feedbacks";
import Header from "./components/header/Header";
import Catalog from "./components/menu/Catalog";
import { changeWidthMenu } from "./redux/actions";
import { useDispatch, useSelector } from "react-redux";

const useStyles = makeStyles({
	root: {
		height: "100%",
		paddingTop: 34,
		display: "flex",
		flexDirection: "column",
		alignItems: "center",
	},
	container: {
		display: "flex",
		flexDirection: "column",
		width: "100%",
		maxWidth: 2000,
		height: "100%",
	},
	main: {
		flexGrow: 1,
		width: "100%",
		display: "flex",
	},
	content: {
		width: "100%",
	},
});

function App() {
	const classes = useStyles();

	// const [drawerWidth, setDrawerWidth] = useState(220);
	const menuWidth = useSelector((state) => state.app.menu);

	// const handleDrawer = () => {
	// 	changeStateMenu(!stateMenu);
	// 	drawerWidth ? setDrawerWidth(0) : setDrawerWidth(220);
	// };

	const openMenu = () => {
		changeWidthMenu(220);
	};
	const closeMenu = () => {
		changeWidthMenu(0);
	};

	return (
		<div className={classes.root}>
			<Router>
				<Header
					openMenu={openMenu}
					closeMenu={closeMenu}
					menuWidth={menuWidth}
				/>
				<div className={classes.container}>
					<main className={classes.main}>
						<Catalog menuWidth={menuWidth} />
						<div id="content" className={classes.content}>
							<Switch>
								<Route exact path="/" component={Main} />
								<Route exact path="/about" component={About} />
								<Route exact path="/contacts" component={Contacts} />
								<Route exact path="/feedbacks" component={Feedbacks} />
							</Switch>
						</div>
					</main>
				</div>
			</Router>
		</div>
	);
}

export default App;
