import React, { useState, useEffect } from "react";
import { makeStyles } from "@material-ui/core/styles";
import Drawer from "@material-ui/core/Drawer";
import List from "@material-ui/core/List";
import ListItem from "@material-ui/core/ListItem";
import ListItemText from "@material-ui/core/ListItemText";
import { colors } from "../../theme/colors";
import { useSelector } from "react-redux";
import CatalogFilter from "./components/CatalogFilter";

const useStyles = makeStyles((menuWidth) => ({
  root: {
    display: "flex",
    flexDirection: "column",
    position: "relative",
    flexGrow: 1,
    maxWidth: (menuWidth) => menuWidth,
    transition: ".2s",
  },
  hide: {
    display: "none",
  },
  drawer: {
    width: (menuWidth) => menuWidth,
    transition: ".2s",
  },
  drawerPaper: {
    padding: 13,
    width: (menuWidth) => menuWidth,
    transition: ".2s",
    borderRight: `1px solid ${colors.secondary}`,
    position: "absolute",
  },
}));

export default function Catalog() {
  const menuWidth = useSelector((state) => state.app.menuWidth);
  const currentPath = useSelector((state) => state.app.location);
  const classes = useStyles(menuWidth);

  const menuItems = [
    {
      name: "Табак",
      subMenu: [
        {
          subName: "Призначення табака",
          subList: [
            "Для самокруток",
            "Бля кальяну",
            "Для люльки",
            "Нюхальний табак",
          ],
        },
        {
          subName: "Бренд",
          subList: [
            "Бренд 1",
            "Бренд 2",
            "Бренд 4",
            "Бренд 5",
            "Бренд 6",
            "Бренд 7",
          ],
        },
      ],
    },
    { Витратники: {} },
    { Сигари: {} },
    { Аксесуари: {} },
  ];

  return currentPath === "/order" ? null : (
    <div className={classes.root}>
      <Drawer
        className={classes.drawer}
        variant="persistent"
        anchor="left"
        open={Boolean(menuWidth)}
        classes={{
          paper: classes.drawerPaper,
        }}
      >
        <List>
          {menuItems.map((menuItem, index) => (
            <div className={classes.filterClass}>
              <h2>menuItem</h2>
              <div className={classes.filtersList}>
                <h3></h3>
              </div>
            </div>
          ))}
          <CatalogFilter filterName="Brand" />
        </List>
      </Drawer>
    </div>
  );
}
