export const cardReducer = (state = initialState, action) => {
  switch (action.type) {
    case FETCH_CARDS:
      return { ...state,
        cards: action.payload
      };
    // case FETCH_ARTICLES:
    // 	return {...state, cards: action.payload}
    // case SET_CURRENT_PAGE:
    // 	return {...state, currentPage: action.payload}

    case ADD_FILTER:
      return { ...state,
        filters: state.filters.concat(action.payload)
      };

    case REMOVE_FILTER:
      // const copyFilters = [...state.filters];
      // copyFilters.splice(action.payload, 0);
      return { ...state,
        filters: state.filters.splice(action.payload, 0)
      };

    default:
      return state;
  }
};
